    public static byte[] computeSHA256TreeHash(File inputFile) throws IOException, 
            NoSuchAlgorithmException { 
        byte[][] chunkSHA256Hashes = getChunkSHA256Hashes(inputFile); 
        return computeSHA256TreeHash(chunkSHA256Hashes); 
    } 
    /** 
     * Computes an SHA256 checksum for each 1 MB chunk of the input file.

This 
     * includes the checksum for the last chunk, even if it's smaller than 1 MB. 
     */ 
    public static byte[][] getChunkSHA256Hashes(File file) throws IOException, 
            NoSuchAlgorithmException { 
        MessageDigest md = MessageDigest.getInstance("SHA-256"); 
        long numChunks = file.length() / ONE_MB; 
        if (file.length() % ONE_MB > 0) { 
            numChunks++; 
        } 
        if (numChunks == 0) { 
            return new byte[][] { md.digest() }; 
        } 
        byte[][] chunkSHA256Hashes = new byte[(int) numChunks][]; 
        FileInputStream fileStream = null; 
        try { 
            fileStream = new FileInputStream(file); 
            byte[] buff = new byte[ONE_MB]; 
            int bytesRead; 
            int idx = 0; 
            while ((bytesRead = fileStream.read(buff, 0, ONE_MB)) > 0) { 
                md.reset(); 
                md.update(buff, 0, bytesRead); 
                chunkSHA256Hashes[idx++] = md.digest(); 
            } 
Upload an Archive by Using Java API Version 2012-06-01 18Amazon S3 Glacier Developer Guide
            return chunkSHA256Hashes; 
        } finally { 
            if (fileStream != null) { 
                try { 
                    fileStream.close(); 
                } catch (IOException ioe) { 
                    System.err.printf("Exception while closing %s.\n %s", 
 file.getName(), 
                            ioe.getMessage()); 
                } 
            } 
        } 
    } 
    /** 
     * Computes the SHA-256 tree hash for the passed array of 1 MB chunk 
     * checksums. 
     */ 
    public static byte[] computeSHA256TreeHash(byte[][] chunkSHA256Hashes) 
            throws NoSuchAlgorithmException { 
        MessageDigest md = MessageDigest.getInstance("SHA-256"); 
        byte[][] prevLvlHashes = chunkSHA256Hashes; 
        while (prevLvlHashes.length > 1) { 
            int len = prevLvlHashes.length / 2; 
            if (prevLvlHashes.length % 2 != 0) { 
                len++; 
            } 
            byte[][] currLvlHashes = new byte[len][]; 
            int j = 0; 
            for (int i = 0; i < prevLvlHashes.length; i = i + 2, j++) { 
                // If there are at least two elements remaining. 
                if (prevLvlHashes.length - i > 1) { 
                    // Calculate a digest of the concatenated nodes. 
                    md.reset(); 
                    md.update(prevLvlHashes[i]); 
                    md.update(prevLvlHashes[i + 1]); 
                    currLvlHashes[j] = md.digest(); 
                } else { // Take care of the remaining odd chunk 
Upload an Archive by Using Java API Version 2012-06-01 19Amazon S3 Glacier Developer Guide
                    currLvlHashes[j] = prevLvlHashes[i]; 
                } 
            } 
            prevLvlHashes = currLvlHashes; 
        } 
        return prevLvlHashes[0]; 
    } 
    /** 
     * Returns the hexadecimal representation of the input byte array 
     */ 
    public static String toHex(byte[] data) { 
        StringBuilder sb = new StringBuilder(data.length * 2); 
        for (byte datum : data) { 
            String hex = Integer.toHexString(datum & 0xFF); 
            if (hex.length() == 1) { 
                // Append leading zero. 
                sb.append("0"); 
            } 
            sb.append(hex); 
        } 
        return sb.toString().toLowerCase(); 
    }
