    public static String toHex(byte[] data) { 
        StringBuilder sb = new StringBuilder(data.length * 2); 
        for (int i = 0; i < data.length; i++) { 
            String hex = Integer.toHexString(data[i] & 0xFF); 
            if (hex.length() == 1) { 
                // Append leading zero. 
                sb.append("0"); 
            } 
            sb.append(hex); 
        } 
        return sb.toString().toLowerCase(); 
    }
}
